import React from 'react';
import "./styles/Help.css";
import { hookstate, useHookstate } from '@hookstate/core';
import ReactModal from 'react-modal';

// In diesem Popup wird die Nutzung der Seite erklärt.

export const popupHelpStatus = hookstate(false)

const Help = () => {
    const status = useHookstate(popupHelpStatus);

    const toggle = () => { status.set(!status) }

    return (
        <ReactModal
            overlayClassName="overlay"
            className="modal-help-content"
            isOpen={status.get()}
            onRequestClose={toggle}
            shouldFocusAfterRender={true}
            shouldCloseOnOverlayClick={true}
            shouldCloseOnEsc={true}
            shouldReturnFocusAfterClose={true}
            preventScroll={true}
            contentLabel={"Popup für Hilfe"}
        >
            <h2>Erklärung der einzelnen Funktionen von StreamWithMe</h2>
            <h3>Einen Raum erstellen</h3>
            <div>Wählen Sie den "Einen Raum Erstellen" Button in der Mitte der Seite an. Sie werden nun aufgefordert einen Benutzernamen einzugeben. Wenn Sie diesen bestätigen,
                wird Ihr Raum erstellt und Sie werden direkt zu Ihrem Raum weitergeleitet.
            </div>
            <h3>Einem Raum beitreten</h3>
            <div>Sie können Räume über die Raumliste, oder einen Einlade-Link betreten</div>
            <h4> Die Raum-Liste</h4>
            <div>Die Raumliste finden Sie auf der Hauptseite im unteren drittel. Hier werden alle derzeit geöffneten Räume, mit verschiedenen Informationen gelistet. Wählen Sie
                einen Raum an um ihm beizutreten. Sie werden nun aufgefordert einen Benutzernamen einzugeben. Wenn Sie diesen bestätigen,
                 werden Sie direkt zu Ihrem Raum weitergeleitet.
            </div>
            <h4> Der Einladelink</h4>
            <div>Einladelinks sind Links, die </div>
            <h3>Der Benutzername</h3>
            <h3>Ein Video Posten</h3>
            <h3>Funktionen des Players</h3>
            <h3>Einen Einlade-Link erstellen</h3>
            <h3>Die User-List</h3>
            <h3>Der Chat</h3>
            <button className="modal-btn" onClick={toggle}>×</button>
        </ReactModal>
    );
};
export default Help;
