import React from 'react';
import RoomListElement from "./RoomListElement";
import './styles/RoomList.css';
import Async from "react-async";
import { getRooms, getRoomUsers } from './API/RoomAPI';
import UserListElement from './UserListElement';

let startIndex = 0;
let endIndex = 35;

const shownRooms = (rooms) => {
    let out = [];
    if (rooms.length === 0) return out;
    for (let i = startIndex; i < endIndex; i++) {
        if (typeof (rooms[i]) == "object")
            out[i] = rooms[i].name;
    }
    return out;
};

const UserList = () => (
    <Async promiseFn={getRoomUsers}>
        {({ data, error, isLoading }) => {
            if (isLoading) return "Loading...";
            if (error) return 'Something went wrong: ' + error.message;
            if (data)
                return (
                    <div className="flex-userList">
                        <div className='userList_container'>
                            <ul className='userList' aria-label="User">
                                {data.users.map(user => (<UserListElement name={user.name} key={user.id} />))}
                            </ul>
                        </div>
                    </div>
                );
            return null;
        }}
    </Async>
);

export default UserList;
