import PostUser from "./PostUser"
import {SetUser} from "../App"
export  const createUser = (_name) => {
fetch('https://gruppe15.toni-barth.com/users/', 
{
    method: "POST",
    headers: {
        "Content-Type": "application/json"
    },
    body: JSON.stringify({
        name: _name
    })
}
)
.then(res => {
    return res.json()
})
.then(data=> SetUser(data.id))
}
export const getUser = () => {
    fetch('https://gruppe15.toni-barth.com/users/', 
    {
        method: "GET",
    })
    .then(res => {
        return res.json()
    })
    .then(data=> console.log(data))
}
export const deleteUser = (userId) =>{
    fetch('https://gruppe15.toni-barth.com/users/'+userId, 
    {
        method: "DELETE"
    })
}
