import React from 'react';
import "./styles/CreateRoom.css"
import {useNavigate} from 'react-router-dom';
import {createRoom} from './API/RoomAPI'
import {setRoomName} from '../Room';
import RoomList from './RoomList';
import Backgroundvideo from './Backgroundvideo';
import { user } from '../App';
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';
import InputUser from './InputUser.js';

let condition
// const navigate = useNavigate();
// const instantiateRoom = useCallback(() => navigate('/Room', {replace: true}), [navigate]);

// const navigate = useNavigate()
// navigate('/Room', { replace: true })

const navigate = useNavigate();
const handleOnClick = useCallback(
  () => navigate("/Room", { replace: true }),
  [navigate]
);

// const instantiateRoom = async () => {

//     if (user===undefined) {
//         condition = true
//         console.log("Blocked join room");
//         return
//     }
//     let name = await createRoom()
//     setRoomName(name)
// }


const CreateRoomUi = () => {
    return (
        <div className="flex-container">
            <div className="flex-inner">
                <Popup trigger={condition} >
                    <InputUser/>
                </Popup>
                <div>
                    <h2 className="accessibility">Einen Raum erstellen</h2>
                    <h2 role="none">enjoy with me.</h2>
                </div>
                <div>
                    <Backgroundvideo />
                    {/* <Link to="/Room"> */}
                        <button type="button" onClick={handleOnClick} id="roombutton">
                            Einen Raum Erstellen
                        </button>
                    {/* </Link> */}
                </div>
            </div>
            <div className="flex-rooms">
                <div><h2>Einem Raum beitreten</h2></div>
                <div className="flex-room">
                    <RoomList/>
                </div>
            </div>
        </div>
    )
}

export default CreateRoomUi
