import React from 'react'
import ReactPlayer from 'react-player'
import { user } from '../App'
import { changeVideoUrl, getVideoUrl, changeVideoStatus, getVideoStatus } from './VideoController'
import { roomName } from '../Room'

class Video extends React.Component {
    constructor(props) {
        super(props);
        this.state = { input: "", url: "" };
        this.handleInputChange = this.handleInputChange.bind(this);
        this.UrlInput = this.UrlInput.bind(this);
    }
    handleInputChange(event) {
        //Update the shown text whilst typing
        this.setState({ input: event.target.value, playing: false });
    }
    async componentDidMount() {
        const interval = setInterval(async () => {
            let dataUrl = await getVideoUrl(roomName)
            let url = dataUrl.url
            if (url != this.state.url) this.setState({ url: url });

            let dataStatus = await getVideoStatus(roomName)
            let status = dataStatus.status
            if (status != this.state.status) this.setState({status:status});

        }, 3000);
        return () => clearInterval(interval);
    }
    async componentDidUpdate() {
        const interval = setInterval(async () => {
            let dataUrl = await getVideoUrl(roomName)
            let url = dataUrl.url
            if (url != this.state.url) this.setState({ url: url });

            let dataStatus = await getVideoStatus(roomName)
            let status = dataStatus.status
            if (status != this.state.status) this.setState({status:status});
            
        }, 3000);
        return () => clearInterval(interval);
    }

    async UrlInput(event) {
        //On Submit create the user and join a room if possible
        if(user!=undefined){
            changeVideoUrl(roomName, user, this.state.input)
            this.setState({ input: "", url: this.state.input });
        }
        event.preventDefault();
    }

    render() {
        return (
            <div className="flex-container">
                <div className="video.input">
                    <form onSubmit={this.UrlInput}>
                        <label htmlFor="Url">
                            URL eingeben
                            <input
                                type='text'
                                name="Url"
                                id="Url"
                                value={this.state.input}
                                onChange={this.handleInputChange} />
                            <input type="submit" value="Video Teilen" />
                        </label>
                    </form>
                    <div role='presentation'>
                        <ReactPlayer 
                            height='3em'
                            width='auto'
                            poster= {this.state.input}
                            url={this.state.input}
                        />
                    </div>
                </div>
                <div className="video">
                    <ReactPlayer
                        height='40em'
                        width='70em'
                        border='bold'
                        controls
                        playing={this.state.playing}
                        url={this.state.url}
                        onReady={() => console.log('onReady callback')}
                        onStart={() => {this.setState({status: "playing"}); changeVideoStatus(roomName, user, 'playing')}}
                        onPause={() => {this.setState({status: "paused"}); changeVideoStatus(roomName, user, 'paused')}}
                        onEnded={() => console.log('onEnd callback')}
                        onError={() => console.log('onError callback')}
                    />
                </div>

            </div>
        )
    }
}
export default Video
