import React,{useCallback} from 'react'
import "./styles/RoomListElement.css";
import {useNavigate} from 'react-router-dom';
import { Link } from "react-router-dom"
import { setRoomName } from '../Room';
import { user } from '../App';

const RoomListElement = (props) => {
    const roomName = props.roomName
    const navigate      = useNavigate();
    const navigateToRoom = useCallback( () => this.navigate("/Room", { replace: true }), [this.navigate] );

    const enterRoom = () => {
        if (user===undefined) {
            console.log("Blocked join room");
            return
        }
        setRoomName(roomName)
        this.navigateToRoom()
    };

    return (
        <li className='listelement' key={roomName} onClick={this.enterRoom}>
            {roomName}
        </li>
    );
}



