import React from 'react';
import { useEffect } from 'react';
import './components/styles/App.css';
import Navbar from './components/Navbar';
import CreateRoom from './components/CreateRoom';
import { Helmet } from 'react-helmet';
import { deleteUser } from './components/UserController';
import Footer from './components/Footer';

const TITLE = 'StreamWithMe'
export let user
export function SetUser(id) {
  //Set the user id and delete the previous if necessary
  if (user != null) {
    deleteUser(user)
  }
  user = id
}

const App = () => {
  useEffect(() => {
    //Delete the current user when the side is closed
    const handleTabClose = event => {
      event.preventDefault();

      console.log('beforeunload event triggered');
      deleteUser(user)
      return (event.returnValue = 'Are you sure you want to exit?');
    };

    window.addEventListener('beforeunload', handleTabClose);

    return () => {
      window.removeEventListener('beforeunload', handleTabClose);
    };
  }, []);
  return (
    <div className="App">
      <Helmet>
        <title>{TITLE}</title>
      </Helmet>
      <Navbar />
      {/* Only show this if the user is not set  */}
      <InputUser trigger={this.state.userState} />
      <CreateRoom />
      <Footer/>
    </div>

  );
}

export default App;
